import React from 'react';
import '../../Header.css';
import cart from '../../../../img/shopping-cart-solid.svg';

class Cart extends React.Component{
  constructor(props){
      super(props);
      this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
      this.props.toggleCart();
  }

  handleSlide = () => {
      this.props.cartIsClicked ? "slideright" : ""
  }

  render(){
      return  <div className="cart">
                  <button onClick={this.handleClick} className={handleSlide}><img src={cart} alt="fa-bars" /></button>
              </div>
  } 
}


export default Cart;
