import React from 'react';
import './App.css';
import Header from '../Header/Header';
import Navigation from '../Navigation/Navigation';
import Form from '../Form/Form';
import Newsletter from '../Newsletter/Newsletter';
import Media from '../Media/Media';
import Footer from '../Footer/Footer';
import SideBar from '../Header/components/Menu/components/SideBar/SideBar';
import CartTotals from '../Header/components/Cart/components/CartTotal/CartTotal';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      sidebarIsClicked: false,
      cartIsClicked: false
    }
  }

  toggleSideBar = () => {
    this.state.sidebarIsClicked ? 
    this.setState({
      sidebarIsClicked: false
    })
    :
    this.setState({
      sidebarIsClicked: true
    })
    console.log(this.state.cartIsClicked)
  }

  toggleCart = () => {
    this.setState({
      cartIsClicked: !this.state.cartIsClicked
    })
    console.log(this.state.cartIsClicked)
  }

  render(){
    return (
      <div className="background">
          <SideBar status={this.state.sidebarIsClicked}/>
          <CartTotals status={this.state.cartIsClicked}/>
          <Header toggleSideBar={this.toggleSideBar} toggleCart={this.toggleCart} />
          <Navigation />
          <Form />
          <Newsletter />
          <Media />
          <Footer />
      </div>
    );
  }
}

export default App;
